local Players = game:GetService("Players")local player = Players.LocalPlayer-- Variables to store checkpointslocal checkpoints = {}local checkpointCounter = 0-- Create the main GUIlocal screenGui = Instance.new("ScreenGui")screenGui.Name = "CheckpointGUI"screenGui.Parent = player:WaitForChild("PlayerGui")local frame = Instance.new("Frame")frame.Size = UDim2.new(0.2, 0, 0.2, 0)frame.Position = UDim2.new(0.1, 0, 0.1, 0)frame.Parent = screenGuilocal createCheckpointButton = Instance.new("TextButton")createCheckpointButton.Size = UDim2.new(1, 0, 0.5, 0)createCheckpointButton.Position = UDim2.new(0, 0, 0, 0)createCheckpointButton.Text = "Create Checkpoint"createCheckpointButton.Parent = framelocal openTeleportGuiButton = Instance.new("TextButton")openTeleportGuiButton.Size = UDim2.new(1, 0, 0.5, 0)openTeleportGuiButton.Position = UDim2.new(0, 0, 0.5, 0)openTeleportGuiButton.Text = "Open Teleport GUI"openTeleportGuiButton.Parent = frame-- Create the teleport GUIlocal teleportScreenGui = Instance.new("ScreenGui")teleportScreenGui.Name = "TeleportGUI"teleportScreenGui.Parent = player:WaitForChild("PlayerGui")teleportScreenGui.Enabled = falselocal teleportFrame = Instance.new("Frame")teleportFrame.Size = UDim2.new(0.2, 0, 0.4, 0)teleportFrame.Position = UDim2.new(0.7, 0, 0.1, 0)teleportFrame.Parent = teleportScreenGuilocal closeButton = Instance.new("TextButton")closeButton.Size = UDim2.new(1, 0, 0.1, 0)closeButton.Position = UDim2.new(0, 0, 0, 0)closeButton.Text = "Close"closeButton.Parent = teleportFramelocal teleportListFrame = Instance.new("ScrollingFrame")teleportListFrame.Size = UDim2.new(1, 0, 0.9, 0)teleportListFrame.Position = UDim2.new(0, 0, 0.1, 0)teleportListFrame.CanvasSize = UDim2.new(0, 0, 5, 0)teleportListFrame.Parent = teleportFrame-- Function to create a checkpointlocal function createCheckpoint()    local character = player.Character or player.CharacterAdded:Wait()    local humanoidRootPart = character:WaitForChild("HumanoidRootPart")    local position = humanoidRootPart.Position    checkpointCounter = checkpointCounter + 1    local checkpointName = "Checkpoint " .. checkpointCounter    -- Store the checkpoint    checkpoints[checkpointName] = position    -- Create a button for the checkpoint in the teleport GUI    local teleportButton = Instance.new("TextButton")    teleportButton.Size = UDim2.new(1, 0, 0, 50)    teleportButton.Text = checkpointName    teleportButton.Parent = teleportListFrame    -- Add functionality to the teleport button    teleportButton.MouseButton1Click:Connect(function()        humanoidRootPart.CFrame = CFrame.new(position)    end)end-- Function to open the teleport GUIlocal function openTeleportGui()    teleportScreenGui.Enabled = trueend-- Function to close the teleport GUIlocal function closeTeleportGui()    teleportScreenGui.Enabled = falseend-- Connect the buttons to their respective functionscreateCheckpointButton.MouseButton1Click:Connect(createCheckpoint)openTeleportGuiButton.MouseButton1Click:Connect(openTeleportGui)closeButton.MouseButton1Click:Connect(closeTeleportGui)-- Create the credits GUIlocal creditsGui = Instance.new("ScreenGui")creditsGui.Name = "CreditsGUI"creditsGui.DisplayOrder = 10  -- Make sure this GUI is on topcreditsGui.Parent = player:WaitForChild("PlayerGui")local creditsFrame = Instance.new("Frame")creditsFrame.Size = UDim2.new(0.3, 0, 0.1, 0)creditsFrame.Position = UDim2.new(0.35, 0, 0, 0)creditsFrame.BackgroundTransparency = 0.5creditsFrame.Parent = creditsGuilocal creditsButton = Instance.new("TextButton")creditsButton.Size = UDim2.new(1, 0, 1, 0)creditsButton.Text = "Credits"creditsButton.Parent = creditsFramelocal creditsText = Instance.new("TextLabel")creditsText.Size = UDim2.new(1, 0, 2, 0)creditsText.Position = UDim2.new(0, 0, 1, 0)creditsText.Text = "Developer: KrakenOfficialBG\nCheckPoint System"creditsText.TextScaled = truecreditsText.BackgroundTransparency = 1creditsText.Visible = falsecreditsText.Parent = creditsFrame-- Function to create a rainbow color effectlocal function rainbowText(textLabel)    local hue = 0    while true do        hue = hue + 0.01        if hue > 1 then hue = 0 end        local color = Color3.fromHSV(hue, 1, 1)        textLabel.TextColor3 = color        wait(0.1)    endend-- Show/hide credits textcreditsButton.MouseButton1Click:Connect(function()    creditsText.Visible = not creditsText.Visibleend)-- Start the rainbow color effectspawn(function()    rainbowText(creditsText)end)